import { useReducer } from 'react';

const initialState = {
  value: '',
  initialState: false,
};

const stateReducerFn = (state, action) => {
  if (action.type === 'INPUT')
  return initialState;
};

const useInput = (validateEnteredValueFn) => {
  const { stateValue, dispatchFn } = useReducer(stateReducerFn, initialState);

  // const [enteredValue, setEnteredValue] = useState('');
  // const [isTouched, setIsTouched] = useState(false);

  const valueIsValid = validateEnteredValueFn(enteredValue);
  const inputIsInvalid = !valueIsValid && isTouched;

  const valueChangeHandler = ({ target: { value } }) => {
    dispatchFn({ type: 'INPUT' });
    // setEnteredValue(value);
    // setIsTouched(true);
  };

  const inputBlurHandler = (event) => {
    setIsTouched(true);
  };

  const reset = () => {
    setEnteredValue('');
    setIsTouched(false);
  };

  return {
    value: enteredValue,
    valueIsValid,
    inputIsInvalid,
    valueChangeHandler,
    inputBlurHandler,
    reset,
  };
};

export default useInput;

