import { useState } from 'react';

const BasicForm = () => {
  const [enteredFirstName, setEnteredFirstName] = useState('');
  const [firstNameInputTouched, setFirstNameInputTouched] = useState(false);
  const [enteredLastName, setEnteredLastName] = useState('');
  const [lastNameInputTouched, setLastNameInputTouchecd] = useState(false);

  const firstNameIsValid = enteredFirstName.trim() !== '';
  const firstNameInputIsInvalid = !firstNameIsValid && firstNameInputTouched;
  const lastNameIsValid = enteredLastName.trim() !== '';
  const lastNameInputIsInvalid = !lastNameIsValid && lastNameInputTouched;

  const firstNameInputChangeHandler = ({ target: { value } }) => {
    setEnteredFirstName(value);
    setFirstNameInputTouched(true);
  };

  const lastNameInputChangeHandler = ({ target: { value } }) => {
    setEnteredLastName(value);
    setLastNameInputTouchecd(true);
  };

  const firstNameInputBlurHandler = (event) => {
    setFirstNameInputTouched(true);
  };

  const firstNameInputClasses = firstNameInputIsInvalid
    ? 'form-control invalid'
    : 'form-control';

  const lastNameInputClasses = lastNameInputIsInvalid
    ? 'form-control invalid'
    : 'form-control';

  return (
    <form>
      <div className='control-group'>
        <div className={firstNameInputClasses}>
          <label htmlFor='name'>First Name</label>
          <input
            type='text'
            id='name'
            onChange={firstNameInputChangeHandler}
            onBlur={firstNameInputBlurHandler}
          />
          {firstNameInputIsInvalid && (
            <p className='error-text'>First Name can't be empty.</p>
          )}
        </div>
        <div className={lastNameInputClasses}>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' onChange={lastNameInputChangeHandler} />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
