import { useState } from 'react';
import useInput from '../hooks/_use-input';

const BasicForm = () => {
  const {
    value: enteredFirstName,
    valueIsValid: firstNameIsValid,
    inputIsInvalid: firstNameInputIsInvalid,
    valueChangeHandler: firstNameInputChangeHandler,
    inputBlurHandler: firstNameInputBlurHandler,
    reset: resetFirstNameInput,
  } = useInput((value) => value.trim() !== '');

  const {
    value: enteredLastName,
    valueIsValid: lastNameIsValid,
    inputIsInvalid: lastNameInputIsInvalid,
    valueChangeHandler: lastNameInputChangeHandler,
    inputBlurHandler: lastNameInputBlurHandler,
    reset: resetLastNameInput,
  } = useInput((value) => value.trim() !== '');

  const [enteredEmail, setEnteredEmail] = useState('');
  const [emailInputTouched, setEmailInputTouched] = useState(false);

  const emailIsValid = enteredEmail.trim().includes('@');
  const emailInputIsInvalid = !emailIsValid && emailInputTouched;

  const emailInputChangeHandler = ({ target: { value } }) => {
    setEnteredEmail(value);
    setEmailInputTouched(true);
  };

  const emailInputBlurHandler = (event) => {
    setEmailInputTouched(true);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    if (!(firstNameIsValid && lastNameIsValid && emailIsValid)) {
      return;
    }

    resetFirstNameInput();
    resetLastNameInput();
    setEnteredEmail('');
    setEmailInputTouched(false);
  };

  let formIsValid = false;

  if (firstNameIsValid && lastNameIsValid && emailIsValid) {
    formIsValid = true;
  }

  const firstNameInputClasses = firstNameInputIsInvalid
    ? 'form-control invalid'
    : 'form-control';

  const lastNameInputClasses = lastNameInputIsInvalid
    ? 'form-control invalid'
    : 'form-control';

  const emailInputClasses = emailInputIsInvalid
    ? 'form-control invalid'
    : 'form-control';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className='control-group'>
        <div className={firstNameInputClasses}>
          <label htmlFor='name'>First Name</label>
          <input
            type='text'
            id='name'
            onChange={firstNameInputChangeHandler}
            onBlur={firstNameInputBlurHandler}
            value={enteredFirstName}
          />
          {firstNameInputIsInvalid && (
            <p className='error-text'>First name can't be empty.</p>
          )}
        </div>
        <div className={lastNameInputClasses}>
          <label htmlFor='name'>Last Name</label>
          <input
            type='text'
            id='name'
            onChange={lastNameInputChangeHandler}
            onBlur={lastNameInputBlurHandler}
            value={enteredLastName}
          />
          {lastNameInputIsInvalid && (
            <p className='error-text'>Last name can't be empty.</p>
          )}
        </div>
      </div>
      <div className={emailInputClasses}>
        <label htmlFor='name'>E-Mail Address</label>
        <input
          type='text'
          id='name'
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
          value={enteredEmail}
        />
        {emailInputIsInvalid && (
          <p className='error-text'>E-mail must contains @</p>
        )}
      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
