import { useState } from 'react';

const useInput = (validateEnteredValueFn) => {
  const [enteredValue, setEnteredValue] = useState('');
  const [isTouched, setIsTouched] = useState(false);

  const valueIsValid = validateEnteredValueFn(enteredValue);
  const inputIsInvalid = !valueIsValid && isTouched;

  const valueChangeHandler = ({ target: { value } }) => {
    setEnteredValue(value);
    setIsTouched(true);
  };

  const inputBlurHandler = (event) => {
    setIsTouched(true);
  };

  const reset = () => {
    setEnteredValue('');
    setIsTouched(false);
  };

  return {
    value: enteredValue,
    valueIsValid,
    inputIsInvalid,
    valueChangeHandler,
    inputBlurHandler,
    reset,
  };
};

export default useInput;
